mapper.update:
update t_sy_balance  set  T_SY_UPDATE_DATE = #{tSyUpdateDate,jdbcType=DATE}

    , T_SY_MONEY =T_SY_MONEY+#{tSyMoney,jdbcType=DECIMAL}


   , T_SY_STATUS = #{tSyStatus,jdbcType=VARCHAR}


   , T_SY_COOL_MONEY = T_SY_COOL_MONEY+#{tSyCoolMoney,jdbcType=DECIMAL}


   , T_SY_COOL_DATE = #{tSyCoolDate,jdbcType=DATE}

   where  T_SY_ACCOUNT = #{tSyAccount,jdbcType=VARCHAR}
